import React from 'react';
import PropTypes from 'prop-types';
import './Movie.css';

function Movie({title,poster,genres,synopsis}){
  return(
    <div classNmae="movie">
      <div classNmae="movie_columns">
        <Movieposter poster={poster} alt={title} />
      </div>
      <div classNmae="movie_columns">
        <h1>{title}</h1>
        <div classNmae="movie_genres">
          {genres.map((genre,index)=><Moviegenre genre={genre} key={index} />)}
        </div>
        <p classNmae="movie_synopsis">
          {synopsis}
        </p>    
      </div>
    </div>
  )
}

function Movieposter({poster,title,alt}){
  return(
    <img src={poster} title={title} alt={alt} classNmae="movie__poster" />
  )
}
function Moviegenre({genre}){
  return(
    <span classNmae="movie_genre">{genre}</span>
  )
}

Movie.propTypes ={
  title: PropTypes.string.isRequired,
  poster: PropTypes.string.isRequired,
  genres: PropTypes.array.isRequired,
  synopsis: PropTypes.string.isRequired
}

Movieposter.propTypes ={
  poster: PropTypes.string.isRequired,
  alt: PropTypes.string.isRequired
}

Moviegenre.propTypes ={
  genre: PropTypes.string.isRequired
}

export default Movie
