import React from 'react';
import PropTypes from 'prop-types';
import './Movie.css';

  // static PropTypes = {
  //   title:PropTypes.string.isRequried,
  //   poster:PropTypes.string.isRequried
  // }


function Movie({title,poster}){
  return(
    <div classNmae="movie">
    <div classNmae="movie_columns">
      <Movieposter poster={poster} />
    </div>
    <div classNmae="movie_columns">
      <h1>{title}</h1>
      <div classNmae="movie_geners">
        {genres.map((geners,index)=><Moviegenre genre={genre} key={index} />)}
      </div>
      <p classNmae="movie_synopsis">
        {synopsis}
      </p>
      
    </div>
    </div>
  )
}

function Movieposter({poster}){
  return(
    <img src={poster} alt={alt} title={title} classNmae="Movie__poster" />
  )
}

function Moviegenre({genre}){
  return(
    <span classNmae="movie_genre">{genre}</span>
  )
}

Movie.propTypes ={
  title: PropTypes.string.isRequired,
  poster: PropTypes.string.isRequired,
  genres: PropTypes.array.isRequired,
  synopsis: PropTypes.string.isRequired
}

Movieposter.propTypes ={
  poster: PropTypes.string.isRequired,
  generes: PropTypes.string.isRequired
}

Moviegenre.propTypes ={
  genre: PropTypes.string.isRequired
}


export default Movie
