import React, { Component } from 'react';
import './App.css';
import Movie from './Movie';

// const movies = [
//   {title:"제목1",poster:"111"},{title:"제목2",poster:"222"},{title:"제목3",poster:"333"}
// ]

class App extends Component {

  state = {
    word : "hello"
  }

  componentDidMount(){
    setTimeout( ()=> {
      this.setState({
        word : "helloword"
      })
    }, 500)
  }

  render() {
    return (
      <div className="App">
        {this.state.word}
      </div>
    );
  }
}

export default App;
