import React, { Component } from 'react';
import './App.css';
import Movie from './Movie';

const movies = [
  {title:"제목1",poster:"111"},{title:"제목2",poster:"222"},{title:"제목3",poster:"333"}
]

class App extends Component {

  state = {
    movies : [
      {title:"제목1",poster:"111"},{title:"제목2",poster:"222"},{title:"제목3",poster:"333"},{title:"hahas",poster:"444"}
    ]
  }

  componentDidMount(){
    setTimeout( ()=> {
      this.setState({
        movies : [
            ...this.state.movies,
        ]
      })
    }, 5000)
  }

  renderMovies = () =>{
    const movie = this.state.movies.map((movie,index)=>{
      return <Movie title={movie.title} poster={movie.poster} key={index} />
    });
    return movies
  }

  render() {
    return (
      <div className="App">
       {this.state.movies ? this.renderMovies() : "loading"}
      </div>
    );
  }
}

export default App;
